INSERT INTO "crops_list" VALUES (37,29,9,'Chayote','<p class=\"\\\\\\&quot;MsoNormal\\\\\\&quot;\" style=\"\\\\\\&quot;text-align:justify;text-indent:.25in\\\\\\&quot;\"><span lang=\"\\\\\\&quot;EN-US\\\\\\&quot;\">In the
